sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var	acrTotalCommandString = '';
	if(current.value == 'Initialized'){
		// call script include to communicate a code referesh
		var queue = new GlideRecord('ecc_queue');
		queue.initialize();
		queue.topic = 'Command';
		queue.queue = 'output';

		acrTotalCommandString += '<parameters> <parameter name="name" value ="php acr/acr.php '+gs.getProperty('x_snc_automated_co.ACRUser4Mid')+' '+gs.getProperty('x_snc_automated_co.ACRUserPwd4MID')+' '+'https://'+gs.getProperty('instance_name')+'.service-now.com ~/'+gs.getProperty('instance_name')+' BusinessRules,ClientScripts,ScriptIncludes,ScriptActions,UIScripts,CatalogClientScripts"/> </parameters>';

		queue.agent = getActiveMidAgentName();

		if(queue.agent == '-1'){
			gs.addErrorMessage('No ACtive MID server message');
			// 	return false;
		}
		queue.payload= acrTotalCommandString;

		queue.insert();

		current.value = 'Processing';
		current.update();

	}


})(current, previous);

function getActiveMidAgentName(){
	var agent = new GlideRecord('ecc_agent');
	var midserverName ='mid.server.';
	agent.initialize();
	agent.addQuery('status','up');
	agent.query();
	if(agent.next()){
		midserverName += agent.name;
	}else{
		midserverName = '-1';
	}
	return midserverName;
}
