sys_script_include.script:
var SyncACRFiles = Class.create();
SyncACRFiles.prototype = {
	initialize: function() {
	},

	copyACRFiles: function(){

		var g = new GlideRecord('sys_ui_page');
		g.addQuery('sys_name','Automated Code Review Artifacts');
		g.query();

		g.next();
		//		gs.print(g.sys_id);


		var gr = new GlideRecord('sys_attachment');
		gr.addQuery('table_sys_id',g.sys_id);
		gr.query();

		while(gr.next()){
			//			gs.print(gr.sys_id)
			var rec = new GlideRecord('sys_attachment');
			rec.get(gr.sys_id+'');


// 			var sa = new GlideRecord('sys_attachment');
// 			sa.get('d8525bb8db3db60043abfd441d961971');

			var binData = new GlideSysAttachment().getBytes(rec);
			var encData = new GlideStringUtil().base64Encode(binData);




			var jspr = new JavascriptProbe('Helsinki_Hareesh');
			jspr.setName('FileToMidServer'); //Any descriptive name will do
			jspr.setJavascript("var ddr = new AttachmentSender();res = ddr.execute();");
			jspr.addParameter("targetFileName",'final.php');
			jspr.addParameter("encodedData",encData);
			jspr.create();
			gs.log('Completed: check Mid Server log');

		}

	},

	type: 'SyncACRFiles'
};
